import java.util.Scanner;

public class DataTypesPrograms {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		System.out.println("Please enter a value:");
		int a = sc.nextInt();
		System.out.println("Please enter b value:");
		int b = sc.nextInt();
		System.out.println("Please enter c value");
		int c = sc.nextInt();
		System.out.println("Please enter d value");
		int d = sc.nextInt();
		System.out.println("Please enter e value");
		int e = sc.nextInt();

		System.out.println("sum of a and b is:"+(a+b));
		System.out.println("Substarction of a and b is:"+(a-b));
		System.out.println("Substarction of a and b is:"+(a-b));
		System.out.println("Multiple of a and b is:"+(a*b));
		System.out.println("Division of a and b is:"+(a/b));
		System.out.println("moduelor Division of a and b is:"+(a%b));
		System.out.println("pre increment of c:"+ (++c));
		System.out.println("pre decrement of c:"+ (--c));
		System.out.println("post increment of c:"+ (c++));
		System.out.println("post decrement of c:"+ (c--));
		System.out.println("d and e equal to each other:"+ (d == e));
		System.out.println("d and e  is not equal to each other:"+ (d != e));
		System.out.println("d and e equal to each other:"+ (d >= e));
		System.out.println("d and e equal to each other:"+ (d <= e));
		
		

		

		



	}

}
